import * as fs from 'fs';
import * as child_process from 'child_process';
import { promisify } from 'util';
import { ExerciseTest, Language, TestResponse, COMPILATION_ERROR_CODE, EXECUTION_ERROR_CODE, UNSUPPORTED_LANGUGAGE, TEST_PASSED_CODE, TEST_FAILED_CODE, TIMEDOUT_CODE } from './lib';
import { TIMEOUT } from 'dns';
import { stderr } from 'process';

const exec = promisify(child_process.exec);
const execFile = promisify(child_process.execFile);

const TIMEOUT_DURATION = 3000; // 5 seconds in milliseconds

// Function to compile and run code based on the detected language
async function compileAndRun(exerciseTest: ExerciseTest, testCode: string, test_case_id: string): Promise<TestResponse> {
    let response: TestResponse = {
        test_case_id: test_case_id,
        reason: "",
        responseCode: ""
    }

    const filePathRegex: RegExp = /src\/\d+\/([^:]+:\d+)/;

    return new Promise(async (resolve, reject) => {
        let compileCommand: string;
        let runCommand: string;

        // Create temporary file path
        const tempFilePath = `src/${exerciseTest.studentID}/temp${test_case_id}.${exerciseTest.language}`
        const executableFilePath = `src/${exerciseTest.studentID}/temp${test_case_id}`

        switch (exerciseTest.language) {
            case Language.C:
                compileCommand = `gcc -o ${executableFilePath} ${tempFilePath} -lcunit`;
                runCommand = `./${executableFilePath}`;
                break;
            default:
                console.error(`Unsupported language: ${exerciseTest.language}`);
                return resolve({
                    test_case_id: test_case_id,
                    reason: "Unsupported langugage",
                    responseCode: `${UNSUPPORTED_LANGUGAGE}`
                });
        }

        fs.writeFileSync(tempFilePath, testCode, 'utf-8');   
        
        // Compile the code
        await exec(compileCommand).catch((reason) => {
            console.error(`Compilation error: ${reason.stderr}`);
            resolve({
                test_case_id: test_case_id,
                reason: reason.stderr,
                responseCode: `${COMPILATION_ERROR_CODE}`
            });
            return;
        });

        // Execute the compiled code
        await execFile(runCommand, {timeout: TIMEOUT_DURATION})
            .then((res) => {
                var result = res.stdout.substring(res.stdout.indexOf("Test"));
                response.reason = result;
                //TODO: better logic for identifieng failure and passes
                if (result.includes("Test failed")){
                    response.responseCode = `${TEST_FAILED_CODE}`;

                    // Use the exec method to extract the file path from the input string
                    response.reason = result.replace(filePathRegex, '');
                }
                else if (result.includes("Test passed"))
                    response.responseCode = `${TEST_PASSED_CODE}`;
                else
                    response.responseCode = "Unknown";
        
                console.log(result)
                resolve(response);
            }, (reason) => {
                console.log("\n\nreason.code = "+ reason + "\n\n");
                if(reason.stderr == ""){
                    return resolve({test_case_id: test_case_id,
                        reason: "Timed out",
                        responseCode: `${TIMEDOUT_CODE}`});
                } else {
                    console.log(`\nExecution stderr: ${reason.stderr}\n`);
                    resolve({test_case_id: test_case_id,
                        reason: reason.stderr,
                        responseCode: `${EXECUTION_ERROR_CODE}`});
                }
                    
            });
    });
}

export {compileAndRun};

import fs from "fs";
//import * as fs from "fs";//Lukas skal køre det sådan her

const COMPILATION_ERROR_CODE = 16;
const EXECUTION_ERROR_CODE = 69;
const UNSUPPORTED_LANGUGAGE = -2;
const TIMEDOUT_CODE = -1;
const TEST_PASSED_CODE = 0;
const TEST_FAILED_CODE = 1;

// Enum representing supported programming languages
enum Language {
    TypeScript = 'typescript',
    JavaScript = 'javascript',
    Python = 'py',
    C = 'c',
}

interface TestResponse {
    test_case_id: string
    reason: string
    responseCode: string
}

interface ExerciseTest {
    language: string;
    code: string;
    studentID: string;
    testCases: TestCase[];
}

interface TestCase {
    testCaseId: string;
    code: string;
}

export {Language, TestResponse, ExerciseTest, COMPILATION_ERROR_CODE, EXECUTION_ERROR_CODE, TEST_PASSED_CODE, UNSUPPORTED_LANGUGAGE, TEST_FAILED_CODE, TIMEDOUT_CODE}

import { appendFileSync, existsSync, mkdirSync, readFileSync } from "node:fs";
import { ExerciseTest } from './lib';
import * as fs from "fs";

// Creates a directory for all resources.
function testRunnerRunner (exerciseTest: ExerciseTest) {
    const { studentID, language, code, testCases } = exerciseTest;

    try {
        createDirectory(`src/${studentID}`);
        createDirectory(`src/${studentID}/tests`);
        createFiles(`src/${studentID}/exerciseFile.${language}`, code);
        testCases.forEach(testCase => {
            createFiles(`src/${studentID}/tests/testFile${testCase.testCaseId}.${language}`, testCase.code, 
                `#include "exerciseFile.${language}"`);
            testCase.code = readFileSync(`src/${studentID}/tests/testFile${testCase.testCaseId}.${language}`, "utf-8");
        });  
    } catch (error) {
        console.error(error);
    }
}

function createDirectory(directoryPath: string): void {
    if (!existsSync(directoryPath)) {
        mkdirSync(directoryPath);
        console.log(`Directory ${directoryPath} created!`);
    }
    else {
        console.error(`Directory ${directoryPath} already exists!`);
    }    
}

function createFiles (directoryPath: string, content: string, include?: string): void {
    if(include)
        appendFileSync(directoryPath, `${include}\n`, "utf8");

    appendFileSync(directoryPath, `${content}\n`, "utf8");
}

export {testRunnerRunner, createDirectory, createFiles};

import * as fs from "fs";
import { testRunnerRunner } from "./converter";
import { COMPILATION_ERROR_CODE, EXECUTION_ERROR_CODE, ExerciseTest, TIMEDOUT_CODE, TestResponse, UNSUPPORTED_LANGUGAGE } from "./lib";
import { compileAndRun } from "./compile_and_run";
import { resolve } from "path";

// receive API call (with JSON object)
// TODO: Replace exerciseTest example with real data (some sort of handling)
const exerciseTest = {
    "language": "c",
    "code": `int addTwoNumbers(int number1, int number2) {
    int sum;
        sum = number1 + number2;
    return sum;
}
    `,
    "studentID": "67890",
    "testCases": [
        { "testCaseId": 1, "code": ` 
#include <CUnit/CUnit.h>
#include <CUnit/Basic.h>
#include <stdio.h>
#include <stdlib.h>

void testAddTwoNumbers(void) {
    CU_ASSERT(addTwoNumbers(1, 2) == 3);
}

int main(void) {
    // Initialize the CUnit test registry
    if (CUE_SUCCESS != CU_initialize_registry()) {
        return CU_get_error();
    }

    // Add a suite to the registry
    CU_pSuite suite = CU_add_suite("Suite_1", NULL, NULL);
    if (suite == NULL) {
        CU_cleanup_registry();
        return CU_get_error();
    }

    // Add the test function to the suite
    if (CU_add_test(suite, "testAddTwoNumbers", testAddTwoNumbers) == NULL) {
        CU_cleanup_registry();
        return CU_get_error();
    }


    // Run the tests using the basic interface
    CU_basic_set_mode(CU_BRM_SILENT);
    CU_basic_run_suite(suite);
    
    int num_failures = CU_get_number_of_failures();

    // Print only if there are failures
    if (num_failures > 0) {
        printf("\\nTest failed\\n Expected:");
        CU_basic_show_failures(CU_get_failure_list());

        printf("\\nActual: %d\\n", addTwoNumbers(1, 2));
    } else {
        printf("Test passed\\n");
    }
}
        `},
        { "testCaseId": 2, "code": `
#include <CUnit/CUnit.h>
#include <CUnit/Basic.h>
#include <stdio.h>
#include <stdlib.h>

void testAddTwoNumbers(void) {
    CU_ASSERT(addTwoNumbers(1, 2) == 2);
}

int main(void) {
    // Initialize the CUnit test registry
    if (CUE_SUCCESS != CU_initialize_registry()) {
        return CU_get_error();
    }

    // Add a suite to the registry
    CU_pSuite suite = CU_add_suite("Suite_1", NULL, NULL);
    if (suite == NULL) {
        CU_cleanup_registry();
        return CU_get_error();
    }

    // Add the test function to the suite
    if (CU_add_test(suite, "testAddTwoNumbers", testAddTwoNumbers) == NULL) {
        CU_cleanup_registry();
        return CU_get_error();
    }


    // Run the tests using the basic interface
    CU_basic_set_mode(CU_BRM_SILENT);
    CU_basic_run_suite(suite);
    
    int num_failures = CU_get_number_of_failures();

    // Print only if there are failures
    if (num_failures > 0) {
        printf("\\nTest failed\\n Expected:");
        CU_basic_show_failures(CU_get_failure_list());

        printf("\\nActual: %d\\n", addTwoNumbers(1, 2));
    } else {
        printf("Test passed\\n");
    }
}
        `}
    ]
};

// Convert JSON to ExerciseTest instance
const exerciseTestJSON = JSON.stringify(exerciseTest, null, 2);
// Parse JSON and cast to interfaces
const parsedExerciseTest: ExerciseTest = JSON.parse(exerciseTestJSON);

async function runCode (exerciseTest: ExerciseTest): Promise<TestResponse[]> {
    // convert parsedExerciseTest to directories and files
    testRunnerRunner(exerciseTest);  

    // Call the async function
    return await runAllTests(exerciseTest);
}

// Compile and run tests
async function runAllTests(exerciseTest: ExerciseTest) : Promise<TestResponse[]> {
    const testResults: TestResponse[] = [];
    try {
        for (const testCase of exerciseTest.testCases) {
            testResults.push((
                await compileAndRun(exerciseTest, testCase.code, testCase.testCaseId)));
            
            console.log(`\ntest result: ${testResults[testResults.length - 1].responseCode}\n`)
            if (testResults[testResults.length - 1].responseCode == (`${COMPILATION_ERROR_CODE}` || `${EXECUTION_ERROR_CODE}`))
                throw new Error("Test failed");
            else if (testResults[testResults.length - 1].responseCode == `${TIMEDOUT_CODE}`) 
                throw new Error("Test timed out");
            else if (testResults[testResults.length - 1].responseCode == `${UNSUPPORTED_LANGUGAGE}`)
                throw new Error("Unsupported language");
        }
    } catch (error) {
        console.error("OUTER ERROR HAS BEEN FOUND: " + error);
    } finally {
        await deleteDirectory(`src/${exerciseTest.studentID}`).catch((error) => {
            throw new Error(`Error deleting directory src/${exerciseTest.studentID}: ${error}`);
        });

        return testResults;
        //console.log(testResults);
        
    }
}

async function deleteDirectory(directoryPath: string): Promise<void> {
    return new Promise((resolve, reject) => {
        fs.rm(directoryPath, { recursive: true }, (err) => {
            if (err) {
                console.error(`Error deleting directory ${directoryPath}: ${err.message}`);
                reject(err);
            } else {
                console.log(`Directory ${directoryPath} deleted successfully.`);
                resolve();
            }
        });
    });
}

