import * as child_process from "child_process";
import { promisify } from "util";
import {
    ExerciseTest,
    Language,
    TestResponse,
    COMPILATION_ERROR_CODE,
    TEST_PASSED_CODE,
    TEST_FAILED_CODE,
    TIMEDOUT_CODE,
    UNKNOWN_FAILURE_CODE,
    UNSUPPORTED_LANGUGAGE,
} from "./lib";

const exec = promisify(child_process.exec);
const execFile = promisify(child_process.execFile);

const TIMEOUT_DURATION = 15000; // 15 seconds in milliseconds

// Function to compile and run code based on the detected language
async function compileAndRun(
    exerciseTest: ExerciseTest,
    test_case_id: string,
): Promise<TestResponse> {
    let response: TestResponse = {
        testCaseId: test_case_id,
        reason: "",
        responseCode: 999,
    };

    return new Promise(async (resolve, reject) => {
        let compileCommand: string;
        let runCommand: string;

        // Create code file path
        const codeFilePath = `${exerciseTest.userId}/testFile${test_case_id}.${exerciseTest.language}`;
        const executableFilePath = `${exerciseTest.userId}/testFile${test_case_id}`;

        switch (exerciseTest.language.toLowerCase()) {
            case Language.C:
                compileCommand = `gcc -o ${executableFilePath} ${codeFilePath} -lcunit`;
                runCommand = `./${executableFilePath}`;
                break;
            default:
                return resolve({
                    testCaseId: test_case_id,
                    reason: "Unsupported langugage",
                    responseCode: UNSUPPORTED_LANGUGAGE,
                });
        }

        // Compile the code
        await exec(compileCommand).catch((reason) => {
            console.error(`Compilation error: ${reason.stderr}`);
            resolve({
                testCaseId: test_case_id,
                reason: reason.stderr,
                responseCode: COMPILATION_ERROR_CODE,
            });
            return;
        });

        // Execute the compiled code
        await execFile(runCommand, { timeout: TIMEOUT_DURATION }).then(
            (res) => {
                var result = res.stdout.substring(res.stdout.indexOf("Test"));
                response.reason = result;
                //TODO: better logic for identifieng failure and passes

                if (result.includes("Test failed"))
                    response.responseCode = TEST_FAILED_CODE;
                else if (result.includes("Test passed"))
                    response.responseCode = TEST_PASSED_CODE;
                else response.responseCode = UNKNOWN_FAILURE_CODE;

                resolve(response);
            },
            (reason) => {
                if (reason.stderr == "") {
                    return resolve({
                        testCaseId: test_case_id,
                        reason: "timed out",
                        responseCode: TIMEDOUT_CODE,
                    });
                } else {
                    console.log(`\nUnknown stderr: ${reason.stderr}\n`);
                    resolve({
                        testCaseId: test_case_id,
                        reason: reason.stderr,
                        responseCode: UNKNOWN_FAILURE_CODE,
                    });
                }
            },
        );
    });
}

export { compileAndRun };

const UNSUPPORTED_LANGUGAGE = 5;
const UNKNOWN_FAILURE_CODE = 4;
const TIMEDOUT_CODE = 3;
const COMPILATION_ERROR_CODE = 2;
const TEST_FAILED_CODE = 1;
const TEST_PASSED_CODE = 0;

// Enum representing supported programming languages
enum Language {
    C = "c",
}

interface TestResponse {
    testCaseId: string;
    reason: string;
    responseCode: number;
}

interface ExerciseTest {
    language: string;
    code: string;
    userId: string;
    testCases: TestCase[];
}

interface TestCase {
    testCaseId: string;
    code: string;
}

export {
    Language,
    TestResponse,
    ExerciseTest,
    UNKNOWN_FAILURE_CODE,
    COMPILATION_ERROR_CODE,
    TEST_PASSED_CODE,
    TEST_FAILED_CODE,
    TIMEDOUT_CODE,
    UNSUPPORTED_LANGUGAGE,
};

import { appendFileSync, existsSync, mkdirSync, readFileSync } from "node:fs";
import { ExerciseTest } from "./lib";

// Creates a directory for all resources.
function testRunnerRunner(exerciseTest: ExerciseTest) {
    const { userId: studentID, language, code, testCases } = exerciseTest;
    createDirectory(studentID);
    createFiles(`${studentID}/exerciseFile.${language}`, code);
    testCases.forEach((testCase) => {
        const inputString = testCase.code;
        const regex = /CU_[^\(]*\(([^)]+)\)/;
        const match = inputString.match(regex);
        const functionName = match ? match[1] + `)` : null;

        createFiles(
            `${studentID}/testFile${testCase.testCaseId}.${language}`,
            `
#include "exerciseFile.${language}"

#include <CUnit/CUnit.h>
#include <CUnit/Basic.h>
#include <stdio.h>
#include <stdlib.h>

void testFunc(void) {
    ${testCase.code}
}

int main(void) {
    // Initialize the CUnit test registry
    if (CUE_SUCCESS != CU_initialize_registry()) {
        return CU_get_error();
    }

    // Add a suite to the registry
    CU_pSuite suite = CU_add_suite("Suite_1", NULL, NULL);
    if (suite == NULL) {
        CU_cleanup_registry();
        return CU_get_error();
    }

    // Add the test function to the suite
    if (CU_add_test(suite, "testFunc", testFunc) == NULL) {
        CU_cleanup_registry();
        return CU_get_error();
    }

    ${testCase.code.split("CU_")[0]}

    // Run the tests using the basic interface
    CU_basic_set_mode(CU_BRM_SILENT);
    CU_basic_run_suite(suite);

    int num_failures = CU_get_number_of_failures();

    // Print only if there are failures
    if (num_failures > 0) {
        printf("\\nTest failed\\n Expected:");
        CU_basic_show_failures(CU_get_failure_list());

        printf("\\nActual: %d\\n", ${functionName});
    } else {
        printf("Test passed\\n");
    }
}`,
        );
        testCase.code = readFileSync(
            `${studentID}/testFile${testCase.testCaseId}.${language}`,
            "utf-8",
        );
    });
}

function createDirectory(directory: string): void {
    const path = `${directory}`;
    if (!existsSync(path)) {
        mkdirSync(path);
        console.log(`Directory ${path} created!`);
    } else console.log(`Directory ${path} already exists!`);
}

function createFiles(
    directoryPath: string,
    content: string,
    include?: string,
): void {
    if (include) appendFileSync(directoryPath, `${include}\n`, "utf8");

    appendFileSync(directoryPath, `${content}\n`, "utf8");
}

export { testRunnerRunner, createFiles, createDirectory };

import * as fs from "fs";
import { testRunnerRunner } from "./converter";
import {
    COMPILATION_ERROR_CODE,
    ExerciseTest,
    TIMEDOUT_CODE,
    TestResponse,
} from "./lib";
import { compileAndRun } from "./compile_and_run";

async function runCode(
    parsedExerciseTest: ExerciseTest,
): Promise<TestResponse[]> {
    // convert parsedExerciseTest to directories and files
    testRunnerRunner(parsedExerciseTest);

    // Call the async function
    return await runAllTests(parsedExerciseTest);
}

// Compile and run tests
async function runAllTests(
    parsedExerciseTest: ExerciseTest,
): Promise<TestResponse[]> {
    const testResults: TestResponse[] = [];
    try {
        for (const testCase of parsedExerciseTest.testCases) {
            testResults.push(
                await compileAndRun(parsedExerciseTest, testCase.testCaseId),
            );

            console.log(
                `\ntest result: ${
                    testResults[testResults.length - 1].responseCode
                }\n`,
            );
            if (
                testResults[testResults.length - 1].responseCode ==
                COMPILATION_ERROR_CODE
            ) {
                throw new Error("Test failed");
            } else if (
                testResults[testResults.length - 1].responseCode ==
                TIMEDOUT_CODE
            ) {
                throw new Error("Test timed out");
            }
        }
    } catch (error) {
        console.error("Error has been found: " + error);
    } finally {
        await deleteDirectory(`${parsedExerciseTest.userId}`).catch((error) => {
            throw new Error(
                `Error deleting directory ${parsedExerciseTest.userId}: ${error}`,
            );
        });

        return testResults;
    }
}

async function deleteDirectory(directoryPath: string): Promise<void> {
    return new Promise((resolve, reject) => {
        fs.rm(directoryPath, { recursive: true }, (err) => {
            if (err) {
                console.error(
                    `Error deleting directory ${directoryPath}: ${err.message}`,
                );
                reject(err);
            } else {
                console.log(`Directory ${directoryPath} deleted successfully.`);
                resolve();
            }
        });
    });
}

export { runCode, runAllTests, deleteDirectory };
